# EG 2nd For loops
import time

nums = [6,51,61,94,67,351,946,5489,4,654,684]

for num in nums:
    print(num)
    div = num/2
    if div > 100:
        print(f"{div} is half of {num}. And it is still a large number!" )
    else:
        print(num)

print("We completed all the numbers!")
time.sleep(3)

for x in range(1,10):
    print(x)
time.sleep(3)

print("Count by twos")
for x in range(2,111111111111111,2):
    print(x)
    time.sleep(0.)

print("Count down")
for x in range(100,66, -1):
    print(x)
    time.sleep(0.1)